import React, { useState, useEffect } from 'react';
import { FaWhatsapp, FaFacebookF, FaTwitter, FaEnvelope } from 'react-icons/fa';

function useLocalUserId() {
  const [id] = useState(() => localStorage.getItem('user_id') || (() => {
    const demoId = 'demo-' + Math.random().toString(36).slice(2, 9);
    localStorage.setItem('user_id', demoId);
    return demoId;
  })());
  return id;
}

function ReferralProgram() {
  const userId = useLocalUserId();
  const [referrals, setReferrals] = useState(() => JSON.parse(localStorage.getItem('referral_history') || '[]'));
  const [copied, setCopied] = useState(false);

  const referralLink = `${window.location.origin}/register?ref=${encodeURIComponent(userId)}`;

  useEffect(() => {
    localStorage.setItem('referral_history', JSON.stringify(referrals));
  }, [referrals]);

  const handleCopy = async () => {
    await navigator.clipboard.writeText(referralLink);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  const addDemoReferral = () => {
    const newRef = {
      id: Math.random().toString(36).slice(2, 9),
      name: 'Friend ' + (referrals.length + 1),
      email: `friend${referrals.length + 1}@example.com`,
      date: new Date().toISOString(),
      rewarded: Math.random() < 0.5,
    };
    setReferrals([newRef, ...referrals]);
  };

  const markRewarded = (id) => {
    setReferrals(referrals.map(r => r.id === id ? { ...r, rewarded: true } : r));
  };

  const totalReferrals = referrals.length;
  const totalRewards = referrals.filter(r => r.rewarded).length;
  const pendingRewards = totalReferrals - totalRewards;

  const qrSvgUrl = `https://chart.googleapis.com/chart?cht=qr&chs=220x220&chl=${encodeURIComponent(referralLink)}&choe=UTF-8`;

  const share = (platform) => {
    const encoded = encodeURIComponent(referralLink);
    let url = '';
    switch (platform) {
      case 'whatsapp':
        url = `https://wa.me/?text=${encoded}`;
        break;
      case 'facebook':
        url = `https://www.facebook.com/sharer/sharer.php?u=${encoded}`;
        break;
      case 'twitter':
        url = `https://twitter.com/intent/tweet?text=Join me! ${encoded}`;
        break;
      case 'email':
        url = `mailto:?subject=Join me!&body=${encoded}`;
        break;
    }
    window.open(url, '_blank', 'noopener,noreferrer,width=600,height=600');
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 p-6 md:p-10">
      <div className="max-w-5xl mx-auto space-y-8">

        {/* Page Header */}
        <h1 className="text-3xl font-bold text-blue-700 dark:text-blue-300">Referral Program</h1>
        <p className="text-gray-700 dark:text-gray-300">Invite your friends and earn rewards. Share your referral link via social media or email.</p>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 sm:grid-cols-3 gap-6">
          <div className="bg-white dark:bg-gray-800 rounded-xl shadow p-5 text-center hover:shadow-md transition">
            <div className="text-2xl font-bold text-blue-600">{totalReferrals}</div>
            <div className="text-gray-500 dark:text-gray-400 mt-1">Total Referrals</div>
          </div>
          <div className="bg-white dark:bg-gray-800 rounded-xl shadow p-5 text-center hover:shadow-md transition">
            <div className="text-2xl font-bold text-green-600">{totalRewards}</div>
            <div className="text-gray-500 dark:text-gray-400 mt-1">Rewards Earned</div>
          </div>
          <div className="bg-white dark:bg-gray-800 rounded-xl shadow p-5 text-center hover:shadow-md transition">
            <div className="text-2xl font-bold text-yellow-600">{pendingRewards}</div>
            <div className="text-gray-500 dark:text-gray-400 mt-1">Pending Rewards</div>
          </div>
        </div>

        {/* Referral Link & QR */}
        <div className="flex flex-col md:flex-row gap-6 items-center">
          <div className="flex-1 flex gap-3">
            <input
              type="text"
              readOnly
              value={referralLink}
              className="flex-1 p-3 rounded-lg border border-gray-300 dark:border-gray-700 text-sm bg-gray-50 dark:bg-gray-900 shadow-sm focus:ring-2 focus:ring-blue-500 focus:outline-none"
            />
            <button
              onClick={handleCopy}
              className={`px-5 py-2 rounded-lg bg-blue-600 text-white font-semibold hover:bg-blue-700 transition`}
            >
              {copied ? 'Copied!' : 'Copy'}
            </button>
          </div>
          <div className="w-44 h-44 bg-white dark:bg-gray-800 rounded-xl flex items-center justify-center shadow-md">
            <img src={qrSvgUrl} alt="QR Code" className="p-3"/>
          </div>
        </div>

        {/* Social Share Buttons */}
        <div className="flex flex-wrap gap-3">
          <button onClick={() => share('whatsapp')} className="flex items-center gap-2 px-4 py-2 bg-green-500 text-white rounded-lg shadow hover:bg-green-600 transition"><FaWhatsapp /> WhatsApp</button>
          <button onClick={() => share('facebook')} className="flex items-center gap-2 px-4 py-2 bg-blue-700 text-white rounded-lg shadow hover:bg-blue-800 transition"><FaFacebookF /> Facebook</button>
          <button onClick={() => share('twitter')} className="flex items-center gap-2 px-4 py-2 bg-sky-400 text-white rounded-lg shadow hover:bg-sky-500 transition"><FaTwitter /> Twitter</button>
          <button onClick={() => share('email')} className="flex items-center gap-2 px-4 py-2 bg-gray-300 dark:bg-gray-700 text-black dark:text-white rounded-lg shadow hover:bg-gray-400 dark:hover:bg-gray-600 transition"><FaEnvelope /> Email</button>
        </div>

        {/* Invite Friends */}
        <div className="text-center">
          <button onClick={addDemoReferral} className="px-6 py-3 bg-blue-600 text-white rounded-xl shadow hover:bg-blue-700 transition font-semibold">
            Invite a Friend
          </button>
        </div>

        {/* Referral History */}
        <div className="bg-white dark:bg-gray-800 rounded-xl shadow p-5 space-y-4">
          <h2 className="text-xl font-semibold text-gray-800 dark:text-gray-200">Referral History</h2>
          {referrals.length === 0 && <div className="text-gray-500 dark:text-gray-400 text-center py-6">No referrals yet</div>}
          {referrals.map(r => (
            <div key={r.id} className="flex flex-col md:flex-row justify-between items-start md:items-center p-4 bg-gray-50 dark:bg-gray-900 rounded-lg shadow-sm hover:shadow-md transition gap-3">
              <div>
                <div className="font-semibold text-gray-800 dark:text-gray-200">{r.name}</div>
                <div className="text-sm text-gray-500 dark:text-gray-400">{r.email}</div>
                <div className="text-xs text-gray-400 dark:text-gray-500">{new Date(r.date).toLocaleString()}</div>
              </div>
              <div className="flex items-center gap-2 mt-2 md:mt-0">
                <div className={`px-2 py-1 text-xs rounded font-semibold ${r.rewarded ? 'bg-green-100 text-green-700' : 'bg-yellow-100 text-yellow-700'}`}>
                  {r.rewarded ? 'Rewarded' : 'Pending'}
                </div>
                {!r.rewarded && (
                  <button onClick={() => markRewarded(r.id)} className="px-3 py-1 bg-blue-600 text-white rounded-lg text-xs font-semibold hover:bg-blue-700 transition">Mark Rewarded</button>
                )}
              </div>
            </div>
          ))}
        </div>

      </div>
    </div>
  );
}

export default ReferralProgram;
