import React, { useState } from 'react';

function TutorUpload() {
  const [videoTitle, setVideoTitle] = useState('');
  const [videoUrl, setVideoUrl] = useState('');
  const [paperTitle, setPaperTitle] = useState('');
  const [paperUrl, setPaperUrl] = useState('');
  const [message, setMessage] = useState('');

  const handleVideoUpload = (e) => {
    e.preventDefault();
    if (!videoTitle || !videoUrl) return setMessage('Please provide both title and video URL.');
    const videos = JSON.parse(localStorage.getItem('tutorial_videos') || '[]');
    videos.push({ title: videoTitle, url: videoUrl });
    localStorage.setItem('tutorial_videos', JSON.stringify(videos));
    setVideoTitle('');
    setVideoUrl('');
    setMessage('Video uploaded!');
  };

  const handlePaperUpload = (e) => {
    e.preventDefault();
    if (!paperTitle || !paperUrl) return setMessage('Please provide both title and paper URL.');
    const papers = JSON.parse(localStorage.getItem('exam_papers') || '[]');
    papers.push({ title: paperTitle, url: paperUrl });
    localStorage.setItem('exam_papers', JSON.stringify(papers));
    setPaperTitle('');
    setPaperUrl('');
    setMessage('Paper uploaded!');
  };

  const pastVideos = JSON.parse(localStorage.getItem('tutorial_videos') || '[]');

  return (
    <div className="min-h-screen bg-neutral-100 dark:bg-neutral-900 text-neutral-900 dark:text-neutral-100 px-4 py-6 sm:px-6 sm:py-10">
      <div className="max-w-md sm:max-w-2xl mx-auto bg-white dark:bg-neutral-800 rounded-xl shadow-lg p-6 sm:p-8 space-y-8">
        <h1 className="text-2xl sm:text-3xl font-bold text-center">Upload Tutorials & Exam Papers</h1>

        {message && (
          <div className="text-center text-sm font-semibold text-green-600 dark:text-green-400">
            {message}
          </div>
        )}

        {/* Upload Tutorial Video */}
        <form onSubmit={handleVideoUpload} className="space-y-4">
          <h2 className="text-lg font-semibold">Upload Tutorial Video</h2>
          <input
            type="text"
            placeholder="Tutorial Title"
            value={videoTitle}
            onChange={(e) => setVideoTitle(e.target.value)}
            className="w-full px-4 py-3 rounded-md border border-neutral-300 dark:border-neutral-700 bg-neutral-50 dark:bg-neutral-900 text-sm"
          />
          <input
            type="url"
            placeholder="Video URL (mp4, webm, etc)"
            value={videoUrl}
            onChange={(e) => setVideoUrl(e.target.value)}
            className="w-full px-4 py-3 rounded-md border border-neutral-300 dark:border-neutral-700 bg-neutral-50 dark:bg-neutral-900 text-sm"
          />
          <input
            type="file"
            accept="video/*"
            className="w-full px-4 py-3 rounded-md border border-neutral-300 dark:border-neutral-700 bg-neutral-50 dark:bg-neutral-900 text-sm"
            onChange={(e) => {
              const file = e.target.files[0];
              if (file) {
                const url = URL.createObjectURL(file);
                setVideoUrl(url);
              }
            }}
          />
          {videoUrl && (
            <div>
              <label className="block mb-1 font-semibold">Preview:</label>
              <video controls src={videoUrl} className="w-full rounded-md bg-black" />
            </div>
          )}
          <button
            type="submit"
            className="w-full py-3 rounded-md bg-neutral-800 text-white dark:bg-neutral-100 dark:text-neutral-900 font-semibold hover:opacity-90 transition"
          >
            Upload Video
          </button>
        </form>

        {/* Past Video Previews */}
        <div>
          <h2 className="text-lg font-semibold mb-2">Past Video Previews</h2>
          {pastVideos.length === 0 ? (
            <p className="text-sm text-neutral-600 dark:text-neutral-400">No tutorials available.</p>
          ) : (
            <div className="space-y-4">
              {pastVideos.map((vid, idx) => (
                <div key={idx} className="bg-neutral-100 dark:bg-neutral-900 rounded-md p-3 shadow">
                  <div className="font-bold mb-1">{vid.title}</div>
                  <video controls src={vid.url} className="w-full rounded-md bg-black" />
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Upload Paper */}
        <form onSubmit={handlePaperUpload} className="space-y-4">
          <h2 className="text-lg font-semibold">Upload Paper</h2>
          <input
            type="text"
            placeholder="Paper Title"
            value={paperTitle}
            onChange={(e) => setPaperTitle(e.target.value)}
            className="w-full px-4 py-3 rounded-md border border-neutral-300 dark:border-neutral-700 bg-neutral-50 dark:bg-neutral-900 text-sm"
          />
          <input
            type="url"
            placeholder="Paper URL (PDF, DOCX, etc)"
            value={paperUrl}
            onChange={(e) => setPaperUrl(e.target.value)}
            className="w-full px-4 py-3 rounded-md border border-neutral-300 dark:border-neutral-700 bg-neutral-50 dark:bg-neutral-900 text-sm"
          />
          <input
            type="file"
            accept=".pdf,.doc,.docx"
            className="w-full px-4 py-3 rounded-md border border-neutral-300 dark:border-neutral-700 bg-neutral-50 dark:bg-neutral-900 text-sm"
            onChange={(e) => {
              const file = e.target.files[0];
              if (file) {
                const url = URL.createObjectURL(file);
                setPaperUrl(url);
              }
            }}
          />
          <button
            type="submit"
            className="w-full py-3 rounded-md bg-neutral-800 text-white dark:bg-neutral-100 dark:text-neutral-900 font-semibold hover:opacity-90 transition"
          >
            Upload Paper
          </button>
        </form>
      </div>
    </div>
  );
}

export default TutorUpload;
