// StudentTutorials.js
import React, { useEffect, useState } from 'react';
import './TutorUpload.css'; // reuse the same styles from TutorUpload

function StudentTutorials() {
  const [videos, setVideos] = useState([]);
  const [papers, setPapers] = useState([]);
  const [filterUploader, setFilterUploader] = useState('All');
  const [search, setSearch] = useState('');
  const [uploaders, setUploaders] = useState([]);

  useEffect(() => {
    const v = JSON.parse(localStorage.getItem('tutorial_videos') || '[]');
    const p = JSON.parse(localStorage.getItem('exam_papers') || '[]');
    setVideos(Array.isArray(v) ? v : []);
    setPapers(Array.isArray(p) ? p : []);

    const names = new Set();
    (Array.isArray(v) ? v : []).forEach(item => { if (item.uploader) names.add(item.uploader); });
    (Array.isArray(p) ? p : []).forEach(item => { if (item.uploader) names.add(item.uploader); });
    setUploaders(['All', ...Array.from(names)]);
  }, []);

  const normalize = (s = '') => s.toString().toLowerCase();
  const matchesFilter = (item) =>
    (filterUploader === 'All' || item.uploader === filterUploader) &&
    (search.trim() === '' || normalize(item.title).includes(normalize(search)));

  const filteredVideos = videos.filter(matchesFilter);
  const filteredPapers = papers.filter(matchesFilter);

  return (
    <div className="upload-page">
      <div className="upload-container">
        <h1 className="upload-title">Tutorials & Exam Papers</h1>

        <div className="search-row" style={{ marginBottom: 14 }}>
          <div style={{ display: 'flex', gap: 8, alignItems: 'center', flex: 1 }}>
            <label htmlFor="uploader" style={{ fontWeight: 600, marginRight: 6 }}>Uploader</label>
            <select
              id="uploader"
              value={filterUploader}
              onChange={(e) => setFilterUploader(e.target.value)}
              className="input-field"
              style={{ maxWidth: 240 }}
            >
              {uploaders.map((u, i) => (
                <option key={i} value={u}>{u}</option>
              ))}
            </select>
          </div>

          <div style={{ display: 'flex', gap: 8, alignItems: 'center', marginLeft: 8 }}>
            <input
              type="search"
              placeholder="Search titles..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              className="input-field"
              style={{ width: 180 }}
            />
            <button
              type="button"
              onClick={() => { setSearch(''); setFilterUploader('All'); }}
              className="upload-button"
              style={{ padding: '8px 10px', background: '#f3f4f6', color: '#111827', fontWeight: 600 }}
            >
              Reset
            </button>
          </div>
        </div>

        {/* Tutorials */}
        <h2 className="section-title" style={{ marginBottom: 8 }}>Tutorials</h2>
        {filteredVideos.length === 0 ? (
          <p className="muted" style={{ marginBottom: 12 }}>No tutorials match your filter.</p>
        ) : (
          <div className="card-grid" style={{ marginBottom: 12 }}>
            {filteredVideos.map((vid, idx) => (
              <div className="card" key={idx}>
                <div className="card-top">
                  <div className="card-title">{vid.title}</div>
                  <div className="card-time">{vid.uploadedAt ? new Date(vid.uploadedAt).toLocaleString() : (vid.uploader || '')}</div>
                </div>
                <video controls src={vid.url} className="card-media" />
                <div className="card-actions">
                  <button className="link-button" onClick={() => window.open(vid.url, '_blank')}>Open</button>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Papers */}
        <h2 className="section-title" style={{ marginBottom: 8 }}>Papers</h2>
        {filteredPapers.length === 0 ? (
          <p className="muted">No papers match your filter.</p>
        ) : (
          <div className="card-grid">
            {filteredPapers.map((paper, idx) => (
              <div className="card" key={idx}>
                <div className="card-top">
                  <div className="card-title">{paper.title}</div>
                  <div className="card-time">{paper.uploadedAt ? new Date(paper.uploadedAt).toLocaleString() : (paper.uploader || '')}</div>
                </div>
                <a className="paper-link" href={paper.url} target="_blank" rel="noopener noreferrer">View Paper</a>
                <div className="card-actions">
                  <button className="link-button" onClick={() => window.open(paper.url, '_blank')}>Open</button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default StudentTutorials;
