import React, { useState, useEffect, useMemo } from 'react';
import { FaWhatsapp, FaFacebookF, FaTwitter, FaEnvelope, FaDownload, FaPrint } from 'react-icons/fa';
import './pages.css';

function useLocalUserId() {
  const [id] = useState(() => localStorage.getItem('user_id') || (() => {
    const demoId = 'demo-' + Math.random().toString(36).slice(2, 9);
    localStorage.setItem('user_id', demoId);
    return demoId;
  })());
  return id;
}

// Generate QR URL using Google Charts
const qrApiUrl = (link, size = 360) =>
  `https://chart.googleapis.com/chart?cht=qr&chs=${size}x${size}&chl=${encodeURIComponent(link)}&choe=UTF-8`;

export default function ReferralProgram() {
  const userId = useLocalUserId();
  const referralLink = useMemo(() => `${window.location.origin}/register?ref=${encodeURIComponent(userId)}`, [userId]);

  const [referrals, setReferrals] = useState(() => JSON.parse(localStorage.getItem('referral_history') || '[]'));
  const [copied, setCopied] = useState(false);
  const [previewOpen, setPreviewOpen] = useState(false);
  const [loadingDownload, setLoadingDownload] = useState(false);
  const [error, setError] = useState(null);

  const qrSize = 220;
  const qrSrc = qrApiUrl(referralLink, qrSize);

  // Persist referrals in localStorage
  useEffect(() => {
    localStorage.setItem('referral_history', JSON.stringify(referrals));
  }, [referrals]);

  const handleCopy = async () => {
    try {
      await navigator.clipboard.writeText(referralLink);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch {
      setError('Failed to copy link.');
    }
  };

  const prepareDownload = async (size = 1024) => {
    setLoadingDownload(true);
    setError(null);
    try {
      const res = await fetch(qrApiUrl(referralLink, size));
      if (!res.ok) throw new Error('Download failed');
      const blob = await res.blob();
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `referral-qr-${Date.now()}.png`;
      document.body.appendChild(a);
      a.click();
      a.remove();
    } catch {
      setError('Could not prepare download.');
    } finally {
      setLoadingDownload(false);
    }
  };

  const handlePrint = () => {
    const w = window.open('', '_blank', 'noopener');
    if (!w) return setError('Unable to open print window.');
    w.document.write(`
      <html>
        <head>
          <title>Print QR</title>
          <style>
            body { display:flex; justify-content:center; align-items:center; height:100vh; margin:0; }
            img { max-width:90vw; max-height:90vh; }
          </style>
        </head>
        <body>
          <img src="${qrApiUrl(referralLink, 1024)}" alt="QR code" />
        </body>
      </html>
    `);
    w.document.close();
    w.focus();
    setTimeout(() => w.print(), 400);
  };

  const share = (platform) => {
    const encoded = encodeURIComponent(referralLink);
    let url = '';
    switch (platform) {
      case 'whatsapp': url = `https://wa.me/?text=${encoded}`; break;
      case 'facebook': url = `https://www.facebook.com/sharer/sharer.php?u=${encoded}`; break;
      case 'twitter': url = `https://twitter.com/intent/tweet?text=Join me! ${encoded}`; break;
      case 'email': url = `mailto:?subject=Join me!&body=${encoded}`; break;
    }
    window.open(url, '_blank', 'noopener,noreferrer,width=600,height=600');
  };

  const totalReferrals = referrals.length;
  const totalRewards = referrals.filter(r => r.rewarded).length;
  const pendingRewards = totalReferrals - totalRewards;

  const addDemoReferral = () => {
    const newRef = {
      id: Math.random().toString(36).slice(2, 9),
      name: 'Friend ' + (referrals.length + 1),
      email: `friend${referrals.length + 1}@example.com`,
      date: new Date().toISOString(),
      rewarded: Math.random() < 0.5,
    };
    setReferrals([newRef, ...referrals]);
  };

  const markRewarded = (id) => {
    setReferrals(referrals.map(r => r.id === id ? { ...r, rewarded: true } : r));
  };

  return (
    <div className="landing-container" style={{ padding: '1rem', minHeight: '100vh', background: '#f9fafb', display: 'flex', justifyContent: 'center' }}>
      <div style={{ maxWidth: 480, width: '100%', background: '#fff', borderRadius: 18, padding: '2rem', boxShadow: '0 2px 12px rgba(37,99,235,0.10)' }}>
        <h1 style={{ fontSize: '1.8rem', color: '#2563eb', textAlign: 'center', marginBottom: '0.5rem' }}>Referral Program</h1>
        <p style={{ color: '#2563eb', textAlign: 'center', marginBottom: '1.5rem' }}>Invite friends and earn rewards!</p>

        {/* Stats */}
        <div style={{ display: 'flex', gap: 8, flexWrap: 'wrap', marginBottom: '1rem', justifyContent: 'center' }}>
          <StatCard title="Total Referrals" value={totalReferrals} color="#2563eb" />
          <StatCard title="Rewards Earned" value={totalRewards} color="#16a34a" />
          <StatCard title="Pending Rewards" value={pendingRewards} color="#facc15" />
        </div>

        {/* Link & QR */}
        <div style={{ display: 'flex', gap: 12, flexWrap: 'wrap', alignItems: 'center', marginBottom: '1rem' }}>
          <div style={{ flex: 1, display: 'flex', gap: 6, flexWrap: 'wrap' }}>
            <input readOnly value={referralLink} style={{ flex: 1, padding: 10, borderRadius: 12, border: '1px solid #cbd5e1' }} />
            <button onClick={handleCopy} style={{ padding: '10px 16px', borderRadius: 24, background: '#2563eb', color: '#fff', fontWeight: 600 }}>
              {copied ? 'Copied!' : 'Copy'}
            </button>
          </div>
          <div style={{ width: qrSize, height: qrSize, borderRadius: 12, background: '#fff', boxShadow: '0 2px 8px rgba(37,99,235,0.1)', display: 'flex', justifyContent: 'center', alignItems: 'center', cursor: 'pointer' }} onClick={() => setPreviewOpen(true)}>
            <img src={qrSrc} alt="Referral QR" style={{ width: '90%', height: '90%' }} />
          </div>
        </div>

        {/* Social Share */}
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: 8, justifyContent: 'center', marginBottom: 16 }}>
          <SocialButton color="#25D366" icon={<FaWhatsapp />} text="WhatsApp" onClick={() => share('whatsapp')} />
          <SocialButton color="#4267B2" icon={<FaFacebookF />} text="Facebook" onClick={() => share('facebook')} />
          <SocialButton color="#1DA1F2" icon={<FaTwitter />} text="Twitter" onClick={() => share('twitter')} />
          <SocialButton color="#9CA3AF" icon={<FaEnvelope />} text="Email" onClick={() => share('email')} />
        </div>

        {/* Download & Print */}
        <div style={{ display: 'flex', gap: 8, flexWrap: 'wrap', justifyContent: 'center', marginBottom: 16 }}>
          <button className="primary-btn" onClick={() => prepareDownload(1024)} disabled={loadingDownload}>
            <FaDownload /> {loadingDownload ? 'Preparing...' : 'Download QR'}
          </button>
          <button className="primary-btn" onClick={handlePrint}><FaPrint /> Print</button>
        </div>

        {/* Invite Demo */}
        <div style={{ textAlign: 'center', marginBottom: 16 }}>
          <button onClick={addDemoReferral} style={{ padding: '12px 24px', borderRadius: 32, background: '#2563eb', color: '#fff', fontWeight: 'bold' }}>Invite a Friend</button>
        </div>

        {/* Referral History */}
        <div>
          <h2 style={{ fontSize: '1rem', color: '#2563eb', marginBottom: 8 }}>Referral History</h2>
          {referrals.length === 0 && <div style={{ textAlign: 'center', color: '#9CA3AF', marginTop: 8 }}>No referrals yet</div>}
          {referrals.map(r => (
            <div key={r.id} style={{ padding: 10, borderRadius: 16, background: '#fefefe', boxShadow: '0 2px 8px rgba(37,99,235,0.08)', marginBottom: 8 }}>
              <div style={{ fontWeight: 600 }}>{r.name}</div>
              <div style={{ fontSize: '0.85rem', color: '#6B7280' }}>{r.email}</div>
              <div style={{ fontSize: '0.75rem', color: '#9CA3AF' }}>{new Date(r.date).toLocaleString()}</div>
              <div style={{ marginTop: 6, display: 'flex', gap: 6, flexWrap: 'wrap' }}>
                <div style={{ padding: '2px 6px', borderRadius: 12, fontSize: '0.75rem', fontWeight: 600, background: r.rewarded ? '#DCFCE7' : '#FEF3C7', color: r.rewarded ? '#166534' : '#92400E' }}>
                  {r.rewarded ? 'Rewarded' : 'Pending'}
                </div>
                {!r.rewarded && <button onClick={() => markRewarded(r.id)} style={{ padding: '4px 10px', borderRadius: 12, background: '#2563eb', color: '#fff', fontSize: '0.75rem' }}>Mark Rewarded</button>}
              </div>
            </div>
          ))}
        </div>

        {/* QR Preview Modal */}
        {previewOpen && (
          <div className="modal-backdrop" onClick={() => setPreviewOpen(false)}>
            <div className="modal" onClick={e => e.stopPropagation()} style={{ maxWidth: 400, padding: 16, background: '#fff', borderRadius: 12 }}>
              <h3 style={{ marginBottom: 12, color: '#2563eb' }}>Referral QR Preview</h3>
              <img src={qrApiUrl(referralLink, 1024)} alt="Large QR" style={{ width: '100%', borderRadius: 12 }} />
              <button onClick={() => setPreviewOpen(false)} style={{ marginTop: 12, padding: 10, borderRadius: 12, background: '#2563eb', color: '#fff', width: '100%' }}>Close</button>
            </div>
          </div>
        )}

        {error && <div style={{ color: '#b91c1c', marginTop: 8 }}>{error}</div>}
      </div>
    </div>
  );
}

/* ----- Helpers ----- */
const StatCard = ({ title, value, color }) => (
  <div style={{ flex: '1 0 30%', padding: 12, background: '#fff', borderRadius: 16, textAlign: 'center', boxShadow: '0 2px 8px rgba(37,99,235,0.08)' }}>
    <div style={{ fontSize: '1.4rem', fontWeight: 'bold', color }}>{value}</div>
    <div style={{ fontSize: '0.85rem', color: '#6B7280', marginTop: 4 }}>{title}</div>
  </div>
);

const SocialButton = ({ color, icon, text, onClick }) => (
  <button onClick={onClick} style={{ display: 'flex', alignItems: 'center', gap: 4, padding: '8px 12px', borderRadius: 16, background: color, color: '#fff', fontSize: '0.85rem', fontWeight: 600, cursor: 'pointer', flex: '1 0 45%' }}>
    {icon} {text}
  </button>
);
