import React, { useState } from 'react';
import './pages.css';

function Payment() {
  const [method, setMethod] = useState('');
  const [amount, setAmount] = useState('');
  const [processing, setProcessing] = useState(false);
  const [success, setSuccess] = useState(false);

  const handlePayment = (e) => {
    e.preventDefault();
    setProcessing(true);
    setTimeout(() => {
      setProcessing(false);
      setSuccess(true);
    }, 1500);
  };

  const renderPaymentForm = () => {
    if (!method) return null;

    return (
      <form onSubmit={handlePayment} style={{ marginTop: '1.5rem' }}>
        <input
          type="number"
          placeholder="Enter amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
          style={{
            width: '100%',
            padding: '12px',
            borderRadius: '8px',
            border: '1px solid #cbd5e1',
            marginBottom: '1rem',
            outline: 'none',
          }}
        />

        {method === 'bank' && (
          <div style={{ marginBottom: '1rem', textAlign: 'left' }}>
            <label style={{ fontWeight: '500', color: '#2563eb' }}>Select Bank</label>
            <select
              style={{
                width: '100%',
                padding: '12px',
                borderRadius: '8px',
                border: '1px solid #cbd5e1',
                marginTop: '0.5rem',
              }}
              required
            >
              <option value="">Choose Bank</option>
              <option>National Bank of Malawi</option>
              <option>FDH Bank</option>
              <option>Standard Bank</option>
              <option>NBS Bank</option>
            </select>
          </div>
        )}

        {method === 'mobile' && (
          <div style={{ marginBottom: '1rem', textAlign: 'left' }}>
            <label style={{ fontWeight: '500', color: '#2563eb' }}>Mobile Money Provider</label>
            <select
              style={{
                width: '100%',
                padding: '12px',
                borderRadius: '8px',
                border: '1px solid #cbd5e1',
                marginTop: '0.5rem',
              }}
              required
            >
              <option value="">Choose Provider</option>
              <option>Airtel Money</option>
              <option>TNM Mpamba</option>
            </select>
          </div>
        )}

        <button
          type="submit"
          disabled={processing}
          style={{
            width: '100%',
            padding: '12px',
            borderRadius: '32px',
            background: processing ? '#9ca3af' : '#16a34a',
            color: '#fff',
            fontWeight: 'bold',
            boxShadow: '0 4px 24px rgba(22,163,74,0.18)',
            cursor: processing ? 'not-allowed' : 'pointer',
            transition: 'all 0.3s',
          }}
        >
          {processing ? 'Processing...' : `Pay with ${method === 'bank' ? 'Bank' : 'Mobile Money'}`}
        </button>
      </form>
    );
  };

  return (
    <div
      className="landing-container"
      style={{
        background: '#f9fafb',
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
      }}
    >
      <div
        style={{
          background: '#fff',
          borderRadius: '18px',
          boxShadow: '0 2px 12px rgba(37,99,235,0.10)',
          padding: '40px 32px',
          maxWidth: '420px',
          width: '90%',
          textAlign: 'center',
        }}
      >
        <h1 style={{ fontSize: '2rem', fontWeight: 'bold', color: '#2563eb', marginBottom: '1rem' }}>Payment</h1>
        <p style={{ color: '#2563eb', marginBottom: '2rem' }}>Choose a payment method below</p>

        {!success ? (
          <>
            <div className="payment-methods" style={{ display: 'flex', gap: '1rem', justifyContent: 'center' }}>
              <PaymentCard
                title="Bank"
                image="https://cdn-icons-png.flaticon.com/512/4228/4228706.png"
                selected={method === 'bank'}
                onClick={() => setMethod('bank')}
              />
              <PaymentCard
                title="Mobile Money"
                image="https://cdn-icons-png.flaticon.com/512/2821/2821637.png"
                selected={method === 'mobile'}
                onClick={() => setMethod('mobile')}
              />
            </div>

            {renderPaymentForm()}
          </>
        ) : (
          <div style={{ color: '#16a34a', fontWeight: '600', marginTop: '1rem' }}>
            âœ… Payment of <strong>${amount}</strong> successful via{' '}
            <strong>{method === 'bank' ? 'Bank' : 'Mobile Money'}</strong>!
          </div>
        )}
      </div>
    </div>
  );
}

const PaymentCard = ({ title, image, selected, onClick }) => (
  <div
    onClick={onClick}
    style={{
      flex: 1,
      background: selected ? '#2563eb' : '#fff',
      color: selected ? '#fff' : '#2563eb',
      border: '2px solid #2563eb',
      borderRadius: '16px',
      padding: '16px 8px',
      textAlign: 'center',
      cursor: 'pointer',
      boxShadow: selected ? '0 4px 24px rgba(37,99,235,0.18)' : '0 2px 8px rgba(37,99,235,0.08)',
      transition: 'all 0.3s ease',
    }}
  >
    <img src={image} alt={title} style={{ width: '50px', height: '50px', marginBottom: '0.5rem' }} />
    <p style={{ fontWeight: '600' }}>{title}</p>
  </div>
);

export default Payment;
