// ReferralFrontend.js
import React, { useEffect, useMemo, useState } from 'react';
import './TutorUpload.css'; // reuse your app styles or swap as needed

// Local-storage keys
const KEY_INVITES = 'rf_invites';
const KEY_REFERRALS = 'rf_referrals';
const KEY_REWARDS = 'rf_rewards';

// Helper utilities
const nowISO = () => new Date().toISOString();
const uid = () => Date.now().toString(36) + Math.random().toString(36).slice(2, 8);
const getCurrentUser = () => ({
  id: localStorage.getItem('user_id') || null,
  name: localStorage.getItem('user_name') || null,
  email: localStorage.getItem('user_email') || null
});

const read = (k) => JSON.parse(localStorage.getItem(k) || '[]');
const write = (k, v) => localStorage.setItem(k, JSON.stringify(v));

// Fallback link builder
const buildReferralLink = (referrerId) =>
  `${window.location.origin}/register?ref=${encodeURIComponent(referrerId)}`;

// Add invite (local simulated send)
function addInvite({ referrerId, referrerName, email, link }) {
  const invites = read(KEY_INVITES);
  const item = {
    id: uid(),
    referrerId,
    referrerName: referrerName || null,
    email,
    link,
    createdAt: nowISO(),
    status: 'sent',
  };
  invites.push(item);
  write(KEY_INVITES, invites);
  return item;
}

// Add referral when someone registers (attach referral)
function addReferral({ referrerId, referredUserId, referredEmail, source = 'link' }) {
  const referrals = read(KEY_REFERRALS);
  // prevent duplicates by email or user id
  if (referredUserId && referrals.some(r => r.referredUserId === referredUserId)) return null;
  if (referredEmail && referrals.some(r => r.referredEmail === referredEmail)) return null;
  const item = {
    id: uid(),
    referrerId,
    referredUserId: referredUserId || null,
    referredEmail: referredEmail || null,
    createdAt: nowISO(),
    status: 'registered', // later: qualified
    source
  };
  referrals.push(item);
  write(KEY_REFERRALS, referrals);
  return item;
}

// Qualify a referral (issue reward)
function qualifyReferral(referralId, { rewardForReferrer = 1, rewardForReferred = 0 } = {}) {
  const referrals = read(KEY_REFERRALS);
  const r = referrals.find(x => x.id === referralId);
  if (!r || r.status === 'qualified') return null;
  r.status = 'qualified';
  r.qualifiedAt = nowISO();
  write(KEY_REFERRALS, referrals);

  const rewards = read(KEY_REWARDS);
  const rewardRec = {
    id: uid(),
    referralId: r.id,
    referrerId: r.referrerId,
    credited: rewardForReferrer,
    createdAt: nowISO(),
    redeemed: false
  };
  rewards.push(rewardRec);
  write(KEY_REWARDS, rewards);
  return { referral: r, reward: rewardRec };
}

// Redeem a reward
function redeemReward(rewardId) {
  const rewards = read(KEY_REWARDS);
  const r = rewards.find(x => x.id === rewardId);
  if (!r || r.redeemed) return false;
  r.redeemed = true;
  r.redeemedAt = nowISO();
  write(KEY_REWARDS, rewards);
  return true;
}

// Export helpers
function exportJson(key) {
  const data = read(key);
  const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
  const url = URL.createObjectURL(blob);
  const a = document.createElement('a');
  a.href = url;
  a.download = `${key}.json`;
  a.click();
  URL.revokeObjectURL(url);
}

// Main Referral program UI
export function ReferralProgram({ promoText = 'Share the link and get 2 free sessions when your friend registers and completes their first session.' }) {
  const user = getCurrentUser();
  const referrerId = user.id || (user.email ? user.email : 'demo_user');
  const [link] = useState(buildReferralLink(referrerId));
  const [copied, setCopied] = useState(false);
  const [inviteEmail, setInviteEmail] = useState('');
  const [msg, setMsg] = useState('');

  useEffect(() => { if (copied) { const t = setTimeout(() => setCopied(false), 2000); return () => clearTimeout(t); } }, [copied]);

  const encoded = encodeURIComponent(link);
  const whatsapp = `https://wa.me/?text=${encoded}`;
  const facebook = `https://www.facebook.com/sharer/sharer.php?u=${encoded}`;
  const twitter = `https://twitter.com/intent/tweet?text=${encodeURIComponent('Join me: ' + link)}`;

  const handleCopy = async () => {
    try {
      await navigator.clipboard.writeText(link);
      setCopied(true);
      setMsg('Link copied to clipboard.');
    } catch {
      setMsg('Copy failed â€” select and copy the link manually.');
    }
  };

  const handleInvite = () => {
    if (!inviteEmail || inviteEmail.indexOf('@') === -1) { setMsg('Enter valid email'); return; }
    addInvite({ referrerId, referrerName: user.name || null, email: inviteEmail, link });
    setMsg(`Invite recorded for ${inviteEmail} (simulated send).`);
    setInviteEmail('');
  };

  return (
    <div className="upload-page" style={{ padding: 16 }}>
      <div className="upload-container" style={{ maxWidth: 760 }}>
        <h1 className="upload-title">Refer & Earn</h1>

        <p style={{ color: '#374151' }}>Share your personal referral link. Rewards are credited when the referred user completes the qualifying action.</p>

        <div style={{ marginTop: 12 }}>
          <label style={{ fontWeight: 700 }}>Your referral link</label>
          <input className="input-field" readOnly value={link} style={{ width: '100%', marginTop: 8 }} />
          <div style={{ display: 'flex', gap: 8, marginTop: 8, flexWrap: 'wrap' }}>
            <button onClick={handleCopy} className="upload-button" style={{ background: '#6366f1', color: '#fff' }}>{copied ? 'Copied' : 'Copy link'}</button>
            <a className="link-button" href={whatsapp} target="_blank" rel="noreferrer">WhatsApp</a>
            <a className="link-button" href={facebook} target="_blank" rel="noreferrer">Facebook</a>
            <a className="link-button" href={twitter} target="_blank" rel="noreferrer">Twitter</a>
          </div>
          <div style={{ marginTop: 12, display: 'flex', gap: 8 }}>
            <input className="input-field" placeholder="friend@example.com" value={inviteEmail} onChange={e => setInviteEmail(e.target.value)} />
            <button className="upload-button" onClick={handleInvite}>Invite by email</button>
          </div>
          {msg && <div style={{ marginTop: 8, color: '#374151' }}>{msg}</div>}

          <div style={{ marginTop: 16, padding: 12, background: '#eef2ff', borderRadius: 8 }}>
            <strong>Promotion</strong>
            <div style={{ marginTop: 6 }}>{promoText}</div>
          </div>
        </div>
      </div>
    </div>
  );
}

// Dashboard for referrer to see their stats and manage rewards
export function ReferralDashboard() {
  const user = getCurrentUser();
  const referrerId = user.id || (user.email ? user.email : 'demo_user');

  const invites = useMemo(() => read(KEY_INVITES).filter(i => i.referrerId === referrerId), [referrerId]);
  const referrals = useMemo(() => read(KEY_REFERRALS).filter(r => r.referrerId === referrerId), [referrerId]);
  const rewards = useMemo(() => read(KEY_REWARDS).filter(w => w.referrerId === referrerId), [referrerId]);

  const pendingRewards = rewards.filter(r => !r.redeemed).length;
  const earnedRewards = rewards.length;
  const invitesSent = invites.length;
  const signups = referrals.length;

  return (
    <div className="upload-page" style={{ padding: 16 }}>
      <div className="upload-container" style={{ maxWidth: 980 }}>
        <h2 className="section-title">Referral Dashboard</h2>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(4, 1fr)', gap: 12 }}>
          <div style={{ background: '#f8fafc', padding: 12, borderRadius: 8 }}>
            <div style={{ fontWeight: 700, fontSize: 20 }}>{invitesSent}</div>
            <div style={{ color: '#6b7280' }}>Invites sent</div>
          </div>
          <div style={{ background: '#f8fafc', padding: 12, borderRadius: 8 }}>
            <div style={{ fontWeight: 700, fontSize: 20 }}>{signups}</div>
            <div style={{ color: '#6b7280' }}>Signups</div>
          </div>
          <div style={{ background: '#f8fafc', padding: 12, borderRadius: 8 }}>
            <div style={{ fontWeight: 700, fontSize: 20 }}>{earnedRewards}</div>
            <div style={{ color: '#6b7280' }}>Rewards earned</div>
          </div>
          <div style={{ background: '#f8fafc', padding: 12, borderRadius: 8 }}>
            <div style={{ fontWeight: 700, fontSize: 20 }}>{pendingRewards}</div>
            <div style={{ color: '#6b7280' }}>Pending rewards</div>
          </div>
        </div>

        <div style={{ marginTop: 16, display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 12 }}>
          <div>
            <h3 style={{ marginBottom: 8 }}>Recent invites</h3>
            {invites.length === 0 ? <div className="muted">No invites</div> : (
              <ul style={{ padding: 0, listStyle: 'none' }}>
                {invites.slice().reverse().map(i => (
                  <li key={i.id} style={{ padding: 8, borderBottom: '1px solid #eef2f3' }}>
                    <div style={{ fontWeight: 700 }}>{i.email}</div>
                    <div style={{ color: '#6b7280', fontSize: 13 }}>{i.status} â€¢ {new Date(i.createdAt).toLocaleString()}</div>
                  </li>
                ))}
              </ul>
            )}
          </div>

          <div>
            <h3 style={{ marginBottom: 8 }}>Recent referrals</h3>
            {referrals.length === 0 ? <div className="muted">No referrals yet</div> : (
              <ul style={{ padding: 0, listStyle: 'none' }}>
                {referrals.slice().reverse().map(r => (
                  <li key={r.id} style={{ padding: 8, borderBottom: '1px solid #eef2f3' }}>
                    <div style={{ fontWeight: 700 }}>{r.referredEmail || r.referredUserId || 'â€”'}</div>
                    <div style={{ color: '#6b7280', fontSize: 13 }}>{r.status} â€¢ {new Date(r.createdAt).toLocaleString()}</div>
                    {r.status !== 'qualified' && (
                      <div style={{ marginTop: 6 }}>
                        <button className="link-button" onClick={() => { qualifyReferral(r.id); window.location.reload(); }}>Mark qualified</button>
                      </div>
                    )}
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>

        <div style={{ marginTop: 16, display: 'flex', gap: 8 }}>
          <button className="upload-button" onClick={() => exportJson(KEY_INVITES)}>Export invites</button>
          <button className="upload-button" onClick={() => exportJson(KEY_REFERRALS)}>Export referrals</button>
          <button className="upload-button" onClick={() => exportJson(KEY_REWARDS)}>Export rewards</button>
        </div>

        <div style={{ marginTop: 16 }}>
          <h3 style={{ marginBottom: 8 }}>Rewards</h3>
          {rewards.length === 0 ? <div className="muted">No rewards yet</div> : (
            <ul style={{ padding: 0, listStyle: 'none' }}>
              {rewards.slice().reverse().map(r => (
                <li key={r.id} style={{ padding: 8, borderBottom: '1px solid #eef2f3' }}>
                  <div style={{ fontWeight: 700 }}>Reward {r.id}</div>
                  <div style={{ color: '#6b7280', fontSize: 13 }}>Credited: {r.credited} â€¢ {new Date(r.createdAt).toLocaleString()}</div>
                  <div style={{ marginTop: 6 }}>
                    {r.redeemed ? <span className="muted">Redeemed {new Date(r.redeemedAt).toLocaleString()}</span> :
                      <button className="upload-button" onClick={() => { redeemReward(r.id); window.location.reload(); }}>Redeem</button>}
                  </div>
                </li>
              ))}
            </ul>
          )}
        </div>

      </div>
    </div>
  );
}

// Registration handler (client-only): call this on your register success page to attach a referral
export function ReferralRegisterHandler({ onDone }) {
  // Reads URL params: ?ref=referrerId
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const ref = params.get('ref');
    if (!ref) { onDone?.(); return; }
    // attach referral using current authenticated user if available
    const user = getCurrentUser();
    addReferral({ referrerId: ref, referredUserId: user.id || null, referredEmail: user.email || null, source: 'link' });
    onDone?.();
  }, [onDone]);

  return null;
}

// Combined export for convenience
export default function ReferralFrontend() {
  return (
    <div>
      <ReferralProgram />
      <ReferralDashboard />
    </div>
  );
}
