import React, { useState } from 'react';

function TutorUpload() {
  const [videoTitle, setVideoTitle] = useState('');
  const [videoUrl, setVideoUrl] = useState('');
  const [paperTitle, setPaperTitle] = useState('');
  const [paperUrl, setPaperUrl] = useState('');
  const [uploaderName, setUploaderName] = useState('');
  const [message, setMessage] = useState('');
  const [videoThumbnail, setVideoThumbnail] = useState(null);

  const saveVideos = (videos) => {
    localStorage.setItem('tutorial_videos', JSON.stringify(videos));
  };

  const savePapers = (papers) => {
    localStorage.setItem('exam_papers', JSON.stringify(papers));
  };

  const handleVideoUpload = (e) => {
    e.preventDefault();
    if (!videoTitle || !videoUrl) return setMessage('Please provide both title and video URL.');
    const videos = JSON.parse(localStorage.getItem('tutorial_videos') || '[]');
    videos.push({
      title: videoTitle,
      url: videoUrl,
      uploadedAt: new Date().toISOString(),
      uploader: uploaderName || 'Tutor',
      thumbnail: videoThumbnail || null,
    });
    saveVideos(videos);
    setVideoTitle('');
    setVideoUrl('');
    setVideoThumbnail(null);
    setMessage('Video uploaded!');
  };

  const handlePaperUpload = (e) => {
    e.preventDefault();
    if (!paperTitle || !paperUrl) return setMessage('Please provide both title and paper URL.');
    const papers = JSON.parse(localStorage.getItem('exam_papers') || '[]');
    papers.push({
      title: paperTitle,
      url: paperUrl,
      uploadedAt: new Date().toISOString(),
      uploader: uploaderName || 'Tutor',
    });
    savePapers(papers);
    setPaperTitle('');
    setPaperUrl('');
    setMessage('Paper uploaded!');
  };

  // generate thumbnail from a local video File object
  const generateVideoThumbnail = (file, callback) => {
    if (!file) return callback(null);
    const url = URL.createObjectURL(file);
    const video = document.createElement('video');
    video.preload = 'metadata';
    video.src = url;
    video.muted = true;
    video.playsInline = true;

    const cleanup = () => {
      try { URL.revokeObjectURL(url); } catch (e) {}
      video.src = '';
    };

    const capture = () => {
      const canvas = document.createElement('canvas');
      const w = video.videoWidth;
      const h = video.videoHeight;
      if (!w || !h) {
        cleanup();
        return callback(null);
      }
      const maxW = 640;
      const scale = w > maxW ? maxW / w : 1;
      canvas.width = Math.floor(w * scale);
      canvas.height = Math.floor(h * scale);
      const ctx = canvas.getContext('2d');
      ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
      const dataUrl = canvas.toDataURL('image/jpeg', 0.7);
      cleanup();
      callback(dataUrl);
    };

    const handleLoaded = () => {
      // choose a frame near 1 second, or start if shorter
      try {
        const t = Math.min(1, Math.max(0, video.duration / 3 || 0));
        video.currentTime = t;
      } catch (err) {
        // fallback
        capture();
      }
    };

    video.addEventListener('loadedmetadata', handleLoaded);
    video.addEventListener('seeked', capture);
    // fallback timeout in case seeked doesn't fire
    setTimeout(() => {
      if (!video.videoWidth) {
        try { capture(); } catch (e) {}
      }
    }, 1500);
  };

  const handleVideoFileChange = (file) => {
    if (!file) return;
    if (!file.type.startsWith('video')) {
      setMessage('Selected file is not a video.');
      return;
    }
    const url = URL.createObjectURL(file);
    setVideoUrl(url);
    generateVideoThumbnail(file, (dataUrl) => {
      if (dataUrl) setVideoThumbnail(dataUrl);
    });
    if (!videoTitle) setVideoTitle(file.name.replace(/\.[^/.]+$/, ''));
  };

  const handlePaperFileChange = (file) => {
    if (!file) return;
    const url = URL.createObjectURL(file);
    setPaperUrl(url);
    if (!paperTitle) setPaperTitle(file.name.replace(/\.[^/.]+$/, ''));
  };

  const pastVideos = JSON.parse(localStorage.getItem('tutorial_videos') || '[]');
  const pastPapers = JSON.parse(localStorage.getItem('exam_papers') || '[]');

  const cardStyle = {
    backgroundColor: '#f0f0f0',
    borderRadius: 12,
    padding: 12,
    boxShadow: '0 2px 6px rgba(0,0,0,0.05)',
    marginBottom: 12,
  };

  const cardTitleStyle = {
    fontWeight: 'bold',
    marginBottom: 8,
    color: '#333',
  };

  const mediaStyle = {
    width: '100%',
    maxWidth: '300px',
    height: 'auto',
    borderRadius: 8,
    background: '#000',
  };

  const deleteButtonStyle = {
    marginTop: 8,
    padding: '6px 12px',
    fontSize: '0.9rem',
    borderRadius: 6,
    backgroundColor: '#d32f2f',
    color: '#fff',
    border: 'none',
    cursor: 'pointer',
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 text-gray-900 dark:text-gray-100 p-8">
      <div className="max-w-2xl mx-auto bg-white dark:bg-gray-800 rounded-xl shadow-lg p-8">
        <h1 className="text-2xl font-bold mb-6 text-blue-700 dark:text-blue-300">
          Upload Tutorials & Exam Papers
        </h1>

        {message && (
          <div className="mb-4 text-green-600 dark:text-green-400 font-semibold">
            {message}
          </div>
        )}

        <div style={{ marginBottom: 12 }}>
          <label style={{ display: 'block', marginBottom: 6, fontWeight: 600 }}>Uploader name</label>
          <input
            type="text"
            placeholder="Your name (will appear to students)"
            value={uploaderName}
            onChange={(e) => setUploaderName(e.target.value)}
            className="input"
            style={{ padding: 12, fontSize: '1rem', borderRadius: 8, width: '100%' }}
          />
        </div>

        {/* Upload Tutorial Video */}
        <form onSubmit={handleVideoUpload} className="mb-8" style={{ display: 'flex', flexDirection: 'column', gap: 12 }}>
          <h2 className="text-xl font-semibold mb-2">Upload Tutorial Video</h2>
          <input
            type="text"
            placeholder="Tutorial Title"
            value={videoTitle}
            onChange={(e) => setVideoTitle(e.target.value)}
            className="input"
            style={{ padding: 12, fontSize: '1rem', borderRadius: 8 }}
          />
          <input
            type="url"
            placeholder="Video URL (mp4, webm, etc)"
            value={videoUrl}
            onChange={(e) => setVideoUrl(e.target.value)}
            className="input"
            style={{ padding: 12, fontSize: '1rem', borderRadius: 8 }}
          />
          <input
            type="file"
            accept="video/*"
            className="input"
            style={{ padding: 12, fontSize: '1rem', borderRadius: 8 }}
            onChange={(e) => handleVideoFileChange(e.target.files[0])}
          />
          {videoUrl && (
            <div className="mb-4">
              <label className="block mb-1 font-semibold">Preview:</label>
              <video controls src={videoUrl} style={mediaStyle} />
            </div>
          )}
          <button type="submit" className="button" style={{ padding: 12, fontSize: '1rem', borderRadius: 8 }}>
            Upload
          </button>
        </form>

        {/* Past Video Previews */}
        <div className="mb-8">
          <h2 className="text-xl font-semibold mb-2">Past Video Previews</h2>
          {pastVideos.length === 0 ? (
            <p>No Tutorials available.</p>
          ) : (
            <div>
              {pastVideos.map((vid, idx) => (
                <div key={idx} style={cardStyle}>
                  <div style={cardTitleStyle}>{vid.title}</div>
                  {vid.thumbnail ? (
                    <img src={vid.thumbnail} alt={vid.title} style={{ width: '100%', maxWidth: 300, borderRadius: 8, marginBottom: 8 }} />
                  ) : (
                    <video controls src={vid.url} style={{ borderRadius: 8, background: '#000', maxWidth: 300 }} />
                  )}
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Upload Paper */}
        <form onSubmit={handlePaperUpload} style={{ display: 'flex', flexDirection: 'column', gap: 12 }}>
          <h2 className="text-xl font-semibold mb-2">Upload Paper</h2>
          <input
            type="text"
            placeholder="Paper Title"
            value={paperTitle}
            onChange={(e) => setPaperTitle(e.target.value)}
            className="input"
            style={{ padding: 12, fontSize: '1rem', borderRadius: 8 }}
          />
          <input
            type="url"
            placeholder="Paper URL (PDF, DOCX, etc)"
            value={paperUrl}
            onChange={(e) => setPaperUrl(e.target.value)}
            className="input"
            style={{ padding: 12, fontSize: '1rem', borderRadius: 8 }}
          />
          <input
            type="file"
            accept="application/pdf,application/msword,application/vnd.openxmlformats-officedocument.wordprocessingml.document"
            className="input"
            style={{ padding: 12, fontSize: '1rem', borderRadius: 8 }}
            onChange={(e) => handlePaperFileChange(e.target.files[0])}
          />
          <button type="submit" className="button" style={{ padding: 12, fontSize: '1rem', borderRadius: 8 }}>
            Upload
          </button>
        </form>

        {/* Past Paper Previews */}
        <div className="mt-8">
          <h2 className="text-xl font-semibold mb-2">Past Papers</h2>
          {pastPapers.length === 0 ? (
            <p>No Papers available.</p>
          ) : (
            <div>
              {pastPapers.map((paper, idx) => (
                <div key={idx} style={cardStyle}>
                  <div style={cardTitleStyle}>{paper.title}</div>
                  <a
                    href={paper.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    style={{
                      display: 'inline-block',
                      marginTop: 8,
                      color: '#1a73e8',
                      textDecoration: 'underline',
                      fontSize: '0.95rem',
                    }}
                  >
                    View Paper
                  </a>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default TutorUpload;
