import React, { useEffect, useState } from 'react';
import { t } from '../i18n/i18n';

function useLocalUserId() {
  const [id] = useState(() => localStorage.getItem('user_id') || (() => {
    const demoId = 'demo-' + Math.random().toString(36).slice(2, 9);
    localStorage.setItem('user_id', demoId);
    return demoId;
  })());
  return id;
}

function ReferralProgram({ lang = 'en' }) {
  const userId = useLocalUserId();
  const [copied, setCopied] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [customMessage, setCustomMessage] = useState('');
  const [referrals, setReferrals] = useState(() => {
    const raw = localStorage.getItem('referral_history');
    return raw ? JSON.parse(raw) : [];
  });

  const referralLink = `${window.location.origin}/register?ref=${encodeURIComponent(userId)}`;
  const encodedLink = encodeURIComponent(referralLink);

  useEffect(() => {
    localStorage.setItem('referral_history', JSON.stringify(referrals));
  }, [referrals]);

  const handleCopy = async () => {
    await navigator.clipboard.writeText(referralLink);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  const handleAddReferralDemo = () => {
    // Mock referral SQL save
    const newRef = {
      id: Math.random().toString(36).slice(2, 9),
      name: 'Friend ' + (referrals.length + 1),
      email: `friend${referrals.length + 1}@example.com`,
      date: new Date().toISOString(),
      rewarded: false
    };
    setReferrals([newRef, ...referrals]);
    // Example: here you could call an API to insert into SQL
    console.log('Referral saved to mock SQL:', newRef);
  };

  const markRewarded = (id) => {
    setReferrals(referrals.map(r => r.id === id ? { ...r, rewarded: true } : r));
  };

  return (
    <div className="landing-container p-4 bg-gray-50 min-h-screen">
      <div className="max-w-3xl mx-auto bg-white rounded-xl shadow-lg p-6">
        <h1 className="text-2xl font-bold text-blue-700 mb-4">{t('referral', lang)}</h1>
        <p className="text-gray-700 mb-6">{t('referralDescription', lang)}</p>

        {/* Referral Link */}
        <div className="flex gap-2 mb-6">
          <input
            type="text"
            value={referralLink}
            readOnly
            className="flex-1 p-3 rounded-lg border border-gray-200 text-sm"
          />
          <button className="px-4 py-2 rounded-lg bg-indigo-600 text-white" onClick={handleCopy}>
            {copied ? t('copied', lang) : t('copy', lang)}
          </button>
        </div>

        {/* Social Share */}
        <div className="flex flex-wrap gap-3 mb-6">
          <a href={`https://wa.me/?text=${encodedLink}`} target="_blank" rel="noreferrer"
             className="px-4 py-2 rounded-lg bg-green-500 text-white text-sm">WhatsApp</a>
          <button onClick={() => window.open(`https://www.facebook.com/sharer/sharer.php?u=${encodedLink}`, '_blank')}
                  className="px-4 py-2 rounded-lg bg-blue-700 text-white text-sm">Facebook</button>
          <button className="px-4 py-2 rounded-lg bg-gray-200" onClick={() => setShowModal(true)}>
            {t('sendEmail', lang)}
          </button>
        </div>

        {/* QR & How it works */}
        <div className="flex flex-col md:flex-row gap-6 mb-6">
          <div className="flex-1 p-4 bg-gray-50 rounded-lg border">
            <div className="font-semibold mb-2">{t('howItWorksTitle', lang)}</div>
            <ul className="text-sm space-y-2 text-gray-700">
              <li>• {t('howItWorks1', lang)}</li>
              <li>• {t('howItWorks2', lang)}</li>
              <li>• {t('howItWorks3', lang)}</li>
            </ul>
          </div>
          <div className="w-full md:w-48 text-center p-4 bg-white rounded-lg border">
            <img src={`https://chart.googleapis.com/chart?cht=qr&chs=220x220&chl=${encodedLink}`} alt="QR code" className="mx-auto mb-3"/>
            <div className="text-xs text-gray-600">{t('scanToRegister', lang)}</div>
          </div>
        </div>

        {/* Referrals Table */}
        <div>
          <div className="flex justify-between items-center mb-3">
            <h2 className="text-lg font-semibold">{t('yourReferrals', lang)}</h2>
            <button className="px-3 py-1 rounded bg-green-500 text-white text-sm" onClick={handleAddReferralDemo}>
              {t('addDemoReferral', lang)}
            </button>
          </div>
          <div className="overflow-x-auto rounded-lg border border-gray-200">
            <table className="w-full text-sm">
              <thead className="bg-gray-50 text-gray-500 text-xs uppercase">
                <tr>
                  <th className="p-3">ID</th>
                  <th className="p-3">{t('name', lang)}</th>
                  <th className="p-3">{t('email', lang)}</th>
                  <th className="p-3">{t('date', lang)}</th>
                  <th className="p-3">{t('rewarded', lang)}</th>
                  <th className="p-3">{t('actions', lang)}</th>
                </tr>
              </thead>
              <tbody>
                {referrals.length === 0 ? (
                  <tr><td colSpan="6" className="p-4 text-center text-gray-600">{t('noReferrals', lang)}</td></tr>
                ) : (
                  referrals.map(r => (
                    <tr key={r.id} className="border-t border-gray-100">
                      <td className="p-3">{r.id}</td>
                      <td className="p-3">{r.name}</td>
                      <td className="p-3">{r.email}</td>
                      <td className="p-3">{new Date(r.date).toLocaleString()}</td>
                      <td className="p-3">{r.rewarded ? t('yes', lang) : t('no', lang)}</td>
                      <td className="p-3">
                        {!r.rewarded && <button className="px-2 py-1 bg-indigo-600 text-white text-xs rounded" onClick={() => markRewarded(r.id)}>
                          {t('markRewarded', lang)}
                        </button>}
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ReferralProgram;
