import React, { useEffect, useState } from 'react';

function StudentTutorials() {
  const [videos, setVideos] = useState([]);
  const [papers, setPapers] = useState([]);
  const [filterUploader, setFilterUploader] = useState('All');
  const [search, setSearch] = useState('');
  const [uploaders, setUploaders] = useState([]);

  useEffect(() => {
    const v = JSON.parse(localStorage.getItem('tutorial_videos') || '[]');
    const p = JSON.parse(localStorage.getItem('exam_papers') || '[]');
    setVideos(Array.isArray(v) ? v : []);
    setPapers(Array.isArray(p) ? p : []);

    const names = new Set();
    (Array.isArray(v) ? v : []).forEach(item => { if (item.uploader) names.add(item.uploader); });
    (Array.isArray(p) ? p : []).forEach(item => { if (item.uploader) names.add(item.uploader); });
    setUploaders(['All', ...Array.from(names)]);
  }, []);

  // derive filtered lists
  const normalize = (s = '') => s.toString().toLowerCase();
  const matchesFilter = (item) =>
    (filterUploader === 'All' || item.uploader === filterUploader) &&
    (search.trim() === '' || normalize(item.title).includes(normalize(search)));

  const filteredVideos = videos.filter(matchesFilter);
  const filteredPapers = papers.filter(matchesFilter);

  const cardStyle = {
    backgroundColor: '#f8fafc',
    borderRadius: 10,
    padding: 12,
    boxShadow: '0 2px 8px rgba(16,24,40,0.04)',
    marginBottom: 12,
  };

  const mediaStyle = {
    width: '100%',
    maxWidth: 320,
    height: 'auto',
    borderRadius: 8,
    background: '#000',
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 text-gray-900 dark:text-gray-100 p-4">
      <div className="max-w-3xl mx-auto bg-white dark:bg-gray-800 rounded-xl shadow-lg p-4 md:p-8">
        <h1 className="text-2xl font-bold mb-6 text-blue-700 dark:text-blue-300 text-center">
          Tutorials & Exam Papers
        </h1>

        {/* Filters */}
        <div className="mb-4 flex flex-col sm:flex-row sm:items-center sm:justify-between gap-3">
          <div className="flex items-center gap-2">
            <label htmlFor="uploader" className="font-medium mr-2">Uploader</label>
            <select
              id="uploader"
              value={filterUploader}
              onChange={(e) => setFilterUploader(e.target.value)}
              className="px-3 py-2 rounded-md border"
            >
              {uploaders.map((u, i) => (
                <option key={i} value={u}>{u}</option>
              ))}
            </select>
          </div>

          <div className="flex items-center gap-2">
            <input
              type="search"
              placeholder="Search titles..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              className="px-3 py-2 rounded-md border"
            />
            <button
              type="button"
              onClick={() => { setSearch(''); setFilterUploader('All'); }}
              className="px-3 py-2 rounded-md bg-gray-200 hover:bg-gray-300"
            >
              Reset
            </button>
          </div>
        </div>

        {/* Tutorials */}
        <h2 className="text-xl font-semibold mb-3">Tutorials</h2>
        {filteredVideos.length === 0 ? (
          <p className="text-center mb-6">No tutorials match your filter.</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
            {filteredVideos.map((vid, idx) => (
              <div key={idx} style={cardStyle} className="rounded-lg">
                <div className="mb-2">
                  <div className="font-bold text-lg">{vid.title}</div>
                  <div className="text-sm text-gray-500">
                    {vid.uploader ? `${vid.uploader}` : 'Unknown uploader'} ·{' '}
                    {vid.uploadedAt ? new Date(vid.uploadedAt).toLocaleString() : ''}
                  </div>
                </div>
                <video controls src={vid.url} style={mediaStyle} />
              </div>
            ))}
          </div>
        )}

        {/* Papers */}
        <h2 className="text-xl font-semibold mb-3">Papers</h2>
        {filteredPapers.length === 0 ? (
          <p className="text-center">No papers match your filter.</p>
        ) : (
          <div className="space-y-4">
            {filteredPapers.map((paper, idx) => (
              <div key={idx} style={cardStyle} className="flex flex-col gap-2">
                <div className="flex items-center justify-between">
                  <div>
                    <div className="font-bold text-lg">{paper.title}</div>
                    <div className="text-sm text-gray-500">
                      {paper.uploader ? `${paper.uploader}` : 'Unknown uploader'} ·{' '}
                      {paper.uploadedAt ? new Date(paper.uploadedAt).toLocaleString() : ''}
                    </div>
                  </div>
                  <div>
                    <a
                      href={paper.url}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-blue-600 dark:text-blue-400 underline font-semibold"
                    >
                      View
                    </a>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default StudentTutorials;
