// TutorUpload.js
import React, { useEffect, useState, useRef } from 'react';
import './TutorUpload.css';

function TutorUpload() {
  const [videoTitle, setVideoTitle] = useState('');
  const [videoUrl, setVideoUrl] = useState('');
  const [paperTitle, setPaperTitle] = useState('');
  const [paperUrl, setPaperUrl] = useState('');
  const [message, setMessage] = useState('');
  const [pastVideos, setPastVideos] = useState([]);
  const [pastPapers, setPastPapers] = useState([]);
  const [query, setQuery] = useState('');
  const [confirm, setConfirm] = useState({ open: false, type: null, index: null });
  const [dragActive, setDragActive] = useState(false);
  const dropRef = useRef(null);

  useEffect(() => {
    setPastVideos(JSON.parse(localStorage.getItem('tutorial_videos') || '[]'));
    setPastPapers(JSON.parse(localStorage.getItem('exam_papers') || '[]'));
  }, []);

  useEffect(() => {
    // auto-clear messages after 2.5s
    if (!message) return;
    const t = setTimeout(() => setMessage(''), 2500);
    return () => clearTimeout(t);
  }, [message]);

  const saveVideos = (videos) => {
    localStorage.setItem('tutorial_videos', JSON.stringify(videos));
    setPastVideos(videos);
  };

  const savePapers = (papers) => {
    localStorage.setItem('exam_papers', JSON.stringify(papers));
    setPastPapers(papers);
  };

  const handleVideoUpload = (e) => {
    e.preventDefault();
    if (!videoTitle || !videoUrl) return setMessage('Please provide both title and video URL.');
    const videos = JSON.parse(localStorage.getItem('tutorial_videos') || '[]');
    videos.push({ title: videoTitle, url: videoUrl, uploadedAt: new Date().toISOString() });
    saveVideos(videos);
    setVideoTitle('');
    setVideoUrl('');
    setMessage('Video uploaded!');
  };

  const handlePaperUpload = (e) => {
    e.preventDefault();
    if (!paperTitle || !paperUrl) return setMessage('Please provide both title and paper URL.');
    const papers = JSON.parse(localStorage.getItem('exam_papers') || '[]');
    papers.push({ title: paperTitle, url: paperUrl, uploadedAt: new Date().toISOString() });
    savePapers(papers);
    setPaperTitle('');
    setPaperUrl('');
    setMessage('Paper uploaded!');
  };

  const openDeleteConfirm = (type, index) => {
    setConfirm({ open: true, type, index });
  };

  const performDelete = () => {
    const { type, index } = confirm;
    if (type === 'video') {
      const videos = JSON.parse(localStorage.getItem('tutorial_videos') || '[]');
      videos.splice(index, 1);
      saveVideos(videos);
      setMessage('Video deleted!');
    } else if (type === 'paper') {
      const papers = JSON.parse(localStorage.getItem('exam_papers') || '[]');
      papers.splice(index, 1);
      savePapers(papers);
      setMessage('Paper deleted!');
    }
    setConfirm({ open: false, type: null, index: null });
  };

  const handleFileInput = (file, target) => {
    if (!file) return;
    const ext = (file.name || '').toLowerCase();
    const url = URL.createObjectURL(file);
    if (target === 'video' && file.type.startsWith('video')) {
      setVideoUrl(url);
      if (!videoTitle) setVideoTitle(file.name.replace(/\.[^/.]+$/, ''));
    } else if (target === 'paper' && (file.type === 'application/pdf' || ext.endsWith('.pdf') || ext.endsWith('.doc') || ext.endsWith('.docx') || file.type.includes('word'))) {
      setPaperUrl(url);
      if (!paperTitle) setPaperTitle(file.name.replace(/\.[^/.]+$/, ''));
    } else {
      setMessage('Unsupported file type for selected field.');
    }
  };

  // Drag and drop handlers
  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setDragActive(true);
    } else if (e.type === 'dragleave') {
      setDragActive(false);
    }
  };

  const handleDrop = (e, target) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    const files = e.dataTransfer.files;
    if (files && files.length > 0) {
      handleFileInput(files[0], target);
    }
  };

  const filteredVideos = pastVideos.filter((v) => v.title.toLowerCase().includes(query.toLowerCase()));
  const filteredPapers = pastPapers.filter((p) => p.title.toLowerCase().includes(query.toLowerCase()));

  return (
    <div className="upload-page">
      <div className="upload-container">
        <h1 className="upload-title">Upload Tutorials & Exam Papers</h1>

        {message && <div className="upload-message">{message}</div>}

        <div className="search-row">
          <input
            type="search"
            placeholder="Search uploaded titles..."
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            className="input-field"
          />
          <div className="counts">
            <span>{pastVideos.length} videos</span>
            <span>{pastPapers.length} papers</span>
          </div>
        </div>

        {/* Drag & Drop Zone (shared) */}
        <div
          ref={dropRef}
          className={`drop-zone ${dragActive ? 'drag-active' : ''}`}
          onDragEnter={handleDrag}
          onDragOver={handleDrag}
          onDragLeave={handleDrag}
          onDrop={(e) => handleDrop(e, 'video')} /* default target video for drop; user can adjust below */
        >
          <p className="drop-text">Drag files here (drop to fill Video fields). Tap the file inputs below to choose files.</p>
        </div>

        {/* Upload Tutorial Video */}
        <form onSubmit={handleVideoUpload} className="upload-form">
          <h2 className="section-title">Upload Tutorial Video</h2>
          <input
            type="text"
            placeholder="Tutorial Title"
            value={videoTitle}
            onChange={(e) => setVideoTitle(e.target.value)}
            className="input-field"
          />
          <input
            type="url"
            placeholder="Video URL (mp4, webm, etc)"
            value={videoUrl}
            onChange={(e) => setVideoUrl(e.target.value)}
            className="input-field"
          />
          <input
            type="file"
            accept="video/*"
            className="input-field file-input"
            onChange={(e) => handleFileInput(e.target.files[0], 'video')}
          />
          {videoUrl && (
            <div className="video-preview">
              <label className="preview-label">Preview:</label>
              <video controls src={videoUrl} style={{ maxWidth: 260 }} />
            </div>
          )}
          <button type="submit" className="upload-button">Upload Video</button>
        </form>

        {/* Past Video Previews */}
        <div className="past-section">
          <h2 className="section-title">Past Video Previews</h2>
          {filteredVideos.length === 0 ? (
            <p className="muted">No tutorials available.</p>
          ) : (
            <div className="card-grid">
              {filteredVideos.map((vid, idx) => (
                <div className="card" key={idx}>
                  <div className="card-top">
                    <div className="card-title">{vid.title}</div>
                    <div className="card-time">{new Date(vid.uploadedAt).toLocaleString()}</div>
                  </div>
                  <video controls src={vid.url} className="card-media" />
                  <div className="card-actions">
                    <button className="link-button" onClick={() => { window.open(vid.url, '_blank'); }}>Open</button>
                    <button className="delete-button" onClick={() => openDeleteConfirm('video', idx)}>Delete</button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Upload Paper */}
        <form onSubmit={handlePaperUpload} className="upload-form">
          <h2 className="section-title">Upload Paper</h2>
          <input
            type="text"
            placeholder="Paper Title"
            value={paperTitle}
            onChange={(e) => setPaperTitle(e.target.value)}
            className="input-field"
          />
          <input
            type="url"
            placeholder="Paper URL (PDF, DOCX, etc)"
            value={paperUrl}
            onChange={(e) => setPaperUrl(e.target.value)}
            className="input-field"
          />
          <input
            type="file"
            accept=".pdf,.doc,.docx,application/pdf,application/msword,application/vnd.openxmlformats-officedocument.wordprocessingml.document"
            className="input-field file-input"
            onChange={(e) => handleFileInput(e.target.files[0], 'paper')}
            onDrop={(e) => handleDrop(e, 'paper')}
          />
          {paperUrl && (
            <div className="paper-preview">
              <label className="preview-label">Preview:</label>
              <a href={paperUrl} target="_blank" rel="noopener noreferrer" className="paper-link">Open Paper</a>
            </div>
          )}
          <button type="submit" className="upload-button">Upload Paper</button>
        </form>

        {/* Past Paper Previews */}
        <div className="past-section">
          <h2 className="section-title">Past Papers</h2>
          {filteredPapers.length === 0 ? (
            <p className="muted">No papers available.</p>
          ) : (
            <div className="card-grid">
              {filteredPapers.map((paper, idx) => (
                <div className="card" key={idx}>
                  <div className="card-top">
                    <div className="card-title">{paper.title}</div>
                    <div className="card-time">{new Date(paper.uploadedAt).toLocaleString()}</div>
                  </div>
                  <a className="paper-link" href={paper.url} target="_blank" rel="noopener noreferrer">View Paper</a>
                  <div className="card-actions">
                    <button className="link-button" onClick={() => { window.open(paper.url, '_blank'); }}>Open</button>
                    <button className="delete-button" onClick={() => openDeleteConfirm('paper', idx)}>Delete</button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Confirmation Modal */}
        {confirm.open && (
          <div className="modal-backdrop" role="dialog" aria-modal="true">
            <div className="modal">
              <h3 className="modal-title">Confirm delete</h3>
              <p className="modal-body">Are you sure you want to delete this {confirm.type}?</p>
              <div className="modal-actions">
                <button className="modal-cancel" onClick={() => setConfirm({ open: false, type: null, index: null })}>Cancel</button>
                <button className="modal-delete" onClick={performDelete}>Delete</button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default TutorUpload;
