import React, { useState } from 'react';
import './pages.css';

function Payment() {
  const [amount, setAmount] = useState('');
  const [processing, setProcessing] = useState(false);
  const [success, setSuccess] = useState(false);

  const handlePayment = (e) => {
    e.preventDefault();
    setProcessing(true);
    setTimeout(() => {
      setProcessing(false);
      setSuccess(true);
    }, 1500);
  };

  return (
    <div className="landing-container" style={{ background: '#f9fafb', minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
      <div style={{
        background: '#fff',
        borderRadius: '18px',
        boxShadow: '0 2px 12px rgba(37,99,235,0.10)',
        padding: '40px 32px',
        maxWidth: '400px',
        width: '90%',
        textAlign: 'center'
      }}>
        <h1 style={{ fontSize: '2rem', fontWeight: 'bold', color: '#2563eb', marginBottom: '1rem' }}>Payment</h1>
        <p style={{ color: '#2563eb', marginBottom: '2rem' }}>Complete your payment securely below.</p>

        {!success ? (
          <form onSubmit={handlePayment}>
            <input
              type="number"
              placeholder="Enter amount"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              required
              style={{
                width: '100%',
                padding: '12px',
                borderRadius: '8px',
                border: '1px solid #cbd5e1',
                marginBottom: '1.2rem',
                outline: 'none'
              }}
            />
            <button
              type="submit"
              disabled={processing}
              style={{
                width: '100%',
                padding: '12px',
                borderRadius: '32px',
                background: processing ? '#9ca3af' : '#16a34a',
                color: '#fff',
                fontWeight: 'bold',
                boxShadow: '0 4px 24px rgba(22,163,74,0.18)',
                cursor: processing ? 'not-allowed' : 'pointer',
                transition: 'all 0.3s'
              }}
            >
              {processing ? 'Processing...' : 'Pay Now'}
            </button>
          </form>
        ) : (
          <div style={{ color: '#16a34a', fontWeight: '600', marginTop: '1rem' }}>
            âœ… Payment of <strong>${amount}</strong> successful!
          </div>
        )}
      </div>
    </div>
  );
}

export default Payment;
