import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaHome, FaBook, FaMoneyBillWave, FaUser, FaQuestionCircle, FaGift } from 'react-icons/fa';

function Navbar({ role }) {
  const navigate = useNavigate();
  const [darkMode, setDarkMode] = React.useState(() => localStorage.getItem('darkMode') === 'true');
  const [menuOpen, setMenuOpen] = React.useState(false);
  const [lang, setLang] = React.useState(localStorage.getItem('lang') || 'en');
  const [clickedLink, setClickedLink] = React.useState('');

  React.useEffect(() => {
    if (darkMode) document.documentElement.classList.add('dark');
    else document.documentElement.classList.remove('dark');
    localStorage.setItem('darkMode', darkMode);
  }, [darkMode]);

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  const toggleDarkMode = () => setDarkMode(dm => !dm);

  const handleLangChange = (e) => {
    setLang(e.target.value);
    localStorage.setItem('lang', e.target.value);
    window.location.reload();
  };

  const links = [
    { icon: <FaHome />, label: 'Home', to: role === 'tutor' ? '/tutor-dashboard' : '/student-dashboard' },
    ...(role === 'tutor'
      ? [
          { icon: <FaBook />, label: 'Sessions', to: '/tutor-sessions' },
          { icon: <FaBook />, label: 'Tutorials', to: '/tutor-upload' },
        ]
      : [
          { icon: <FaBook />, label: 'Book Session', to: '/book-session' },
          { icon: <FaBook />, label: 'Tutorials', to: '/student-tutorials' },
          { icon: <FaMoneyBillWave />, label: 'Payment', to: '/payment' },
        ]),
    { icon: <FaGift />, label: 'Referral', to: '/referral' },
    { icon: <FaQuestionCircle />, label: 'Help', to: '/help' },
    role === 'tutor'
      ? { icon: <FaUser />, label: 'Profile', to: `/tutor-profile/${localStorage.getItem('tutor_id') || 'demo-tutor'}` }
      : { icon: <FaUser />, label: 'Profile', to: '/student-profile' },
  ];

  // Handle mobile click to show label temporarily
  const handleMobileClick = (label) => {
    setClickedLink(label);
    setTimeout(() => setClickedLink(''), 2000); // hide after 2 sec
  };

  return (
    <nav style={navbarStyle}>
      {/* Desktop Navbar */}
      <div className="desktop-menu" style={desktopMenuStyle}>
        {links.map(link => (
          <Link key={link.label} to={link.to} style={desktopLinkStyle}>
            {link.icon} <span>{link.label}</span>
          </Link>
        ))}
        <select value={lang} onChange={handleLangChange} style={selectStyle}>
          <option value="en">EN</option>
          <option value="es">ES</option>
          <option value="fr">FR</option>
          <option value="de">DE</option>
          <option value="ny">Chic</option>
          <option value="sw">Swah</option>
        </select>
        <button onClick={toggleDarkMode} style={buttonStyle}>{darkMode ? 'Light Mode' : 'Dark Mode'}</button>
        <button onClick={handleLogout} style={buttonStyle}>Logout</button>
      </div>

      {/* Mobile Navbar */}
      <div className="mobile-menu" style={mobileMenuStyle}>
        {links.map(link => (
          <div key={link.label} style={mobileIconWrapper}>
            <Link to={link.to} onClick={() => handleMobileClick(link.label)} style={mobileLinkStyle}>
              {link.icon}
            </Link>
            {clickedLink === link.label && (
              <span style={mobileLabelStyle}>{link.label}</span>
            )}
          </div>
        ))}
        <button className="navbar-hamburger" onClick={() => setMenuOpen(m => !m)} style={hamburgerStyle}>&#9776;</button>
        {menuOpen && (
          <div style={{ ...mobileDropdownStyle, background: darkMode ? '#334155' : '#fff' }}>
            {links.map(link => (
              <Link key={link.label} to={link.to} onClick={() => setMenuOpen(false)} style={mobileDropdownLinkStyle(darkMode)}>
                {link.label}
              </Link>
            ))}
            <select value={lang} onChange={handleLangChange} style={selectStyle}>
              <option value="en">EN</option>
              <option value="es">ES</option>
              <option value="fr">FR</option>
              <option value="de">DE</option>
              <option value="ny">Chic</option>
              <option value="sw">Swah</option>
            </select>
            <button onClick={toggleDarkMode} style={buttonStyle}>{darkMode ? 'Light Mode' : 'Dark Mode'}</button>
            <button onClick={() => { handleLogout(); setMenuOpen(false); }} style={buttonStyle}>Logout</button>
          </div>
        )}
      </div>
    </nav>
  );
}

/* ----- Styles ----- */
const navbarStyle = { display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '0.5rem 1rem', position: 'relative' };
const desktopMenuStyle = { display: 'flex', alignItems: 'center', gap: '1rem' };
const mobileMenuStyle = { display: 'flex', alignItems: 'center', gap: '1rem', flexWrap: 'nowrap' };
const desktopLinkStyle = { display: 'flex', alignItems: 'center', gap: '0.3rem', textDecoration: 'none', color: '#222', fontSize: '1rem' };
const mobileIconWrapper = { position: 'relative', display: 'flex', flexDirection: 'column', alignItems: 'center' };
const mobileLinkStyle = { fontSize: '1.5rem', color: '#222', textDecoration: 'none' };
const mobileLabelStyle = { position: 'absolute', top: '-24px', background: '#2563eb', color: '#fff', padding: '2px 6px', borderRadius: 4, fontSize: '0.75rem', whiteSpace: 'nowrap' };
const selectStyle = { padding: '4px 8px', borderRadius: 6 };
const buttonStyle = { padding: '6px 12px', borderRadius: 6, cursor: 'pointer', background: '#2563eb', color: '#fff', border: 'none' };
const hamburgerStyle = { fontSize: '2rem', background: 'none', border: 'none', cursor: 'pointer' };
const mobileDropdownStyle = { position: 'absolute', top: '60px', right: '16px', minWidth: '200px', borderRadius: 8, padding: '12px 0', boxShadow: '0 8px 24px rgba(0,0,0,0.15)', display: 'flex', flexDirection: 'column', gap: '0.5rem', zIndex: 9999 };
const mobileDropdownLinkStyle = (darkMode) => ({ padding: '6px 12px', color: darkMode ? '#fff' : '#222', textDecoration: 'none' });

export default Navbar;
