import React, { useState } from 'react';
import './pages.css';

function Payment() {
  const [method, setMethod] = useState('');
  const [amount, setAmount] = useState(null);
  const [processing, setProcessing] = useState(false);
  const [success, setSuccess] = useState(false);

  const sessionOptions = [
    { label: '1 Session (1 hour)', value: 2000 },
    { label: '3 Sessions', value: 5000 },
  ];

  const handlePayment = (e) => {
    e.preventDefault();
    setProcessing(true);
    setTimeout(() => {
      setProcessing(false);
      setSuccess(true);
    }, 1500);
  };

  return (
    <div
      className="landing-container"
      style={{
        background: '#f9fafb',
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
      }}
    >
      <div
        style={{
          background: '#fff',
          borderRadius: '18px',
          boxShadow: '0 2px 12px rgba(37,99,235,0.10)',
          padding: '40px 32px',
          maxWidth: '420px',
          width: '90%',
          textAlign: 'center',
        }}
      >
        <h1 style={{ fontSize: '2rem', fontWeight: 'bold', color: '#2563eb', marginBottom: '1rem' }}>
          Payment
        </h1>
        <p style={{ color: '#2563eb', marginBottom: '2rem' }}>Choose a session package and payment method</p>

        {!success ? (
          <>
            {/* Session Amount Cards */}
            <div style={{ display: 'flex', gap: '1rem', justifyContent: 'center', marginBottom: '1.5rem' }}>
              {sessionOptions.map((option) => (
                <div
                  key={option.value}
                  onClick={() => setAmount(option.value)}
                  style={{
                    flex: 1,
                    background: amount === option.value ? '#2563eb' : '#fff',
                    color: amount === option.value ? '#fff' : '#2563eb',
                    border: '2px solid #2563eb',
                    borderRadius: '16px',
                    padding: '16px 8px',
                    textAlign: 'center',
                    cursor: 'pointer',
                    boxShadow: amount === option.value ? '0 4px 24px rgba(37,99,235,0.18)' : '0 2px 8px rgba(37,99,235,0.08)',
                    transition: 'all 0.3s ease',
                  }}
                >
                  <p style={{ fontWeight: '600' }}>{option.label}</p>
                  <p>K{option.value.toLocaleString()}</p>
                </div>
              ))}
            </div>

            {/* Payment Method Cards */}
            <div className="payment-methods" style={{ display: 'flex', gap: '1rem', justifyContent: 'center', marginBottom: '1.5rem' }}>
              <PaymentCard
                title="Bank"
                image="https://cdn-icons-png.flaticon.com/512/4228/4228706.png"
                selected={method === 'bank'}
                onClick={() => setMethod('bank')}
              />
              <PaymentCard
                title="Mobile Money"
                image="https://cdn-icons-png.flaticon.com/512/2821/2821637.png"
                selected={method === 'mobile'}
                onClick={() => setMethod('mobile')}
              />
            </div>

            {/* Show pay button only if both method and amount are selected */}
            {amount && method && (
              <button
                onClick={handlePayment}
                disabled={processing}
                style={{
                  width: '100%',
                  padding: '12px',
                  borderRadius: '32px',
                  background: processing ? '#9ca3af' : '#16a34a',
                  color: '#fff',
                  fontWeight: 'bold',
                  boxShadow: '0 4px 24px rgba(22,163,74,0.18)',
                  cursor: processing ? 'not-allowed' : 'pointer',
                  transition: 'all 0.3s',
                }}
              >
                {processing ? 'Processing...' : `Pay K${amount.toLocaleString()} via ${method === 'bank' ? 'Bank' : 'Mobile Money'}`}
              </button>
            )}
          </>
        ) : (
          <div style={{ color: '#16a34a', fontWeight: '600', marginTop: '1rem' }}>
            âœ… Payment of <strong>K{amount.toLocaleString()}</strong> successful via{' '}
            <strong>{method === 'bank' ? 'Bank' : 'Mobile Money'}</strong>!
          </div>
        )}
      </div>
    </div>
  );
}

const PaymentCard = ({ title, image, selected, onClick }) => (
  <div
    onClick={onClick}
    style={{
      flex: 1,
      background: selected ? '#2563eb' : '#fff',
      color: selected ? '#fff' : '#2563eb',
      border: '2px solid #2563eb',
      borderRadius: '16px',
      padding: '16px 8px',
      textAlign: 'center',
      cursor: 'pointer',
      boxShadow: selected ? '0 4px 24px rgba(37,99,235,0.18)' : '0 2px 8px rgba(37,99,235,0.08)',
      transition: 'all 0.3s ease',
    }}
  >
    <img src={image} alt={title} style={{ width: '50px', height: '50px', marginBottom: '0.5rem' }} />
    <p style={{ fontWeight: '600' }}>{title}</p>
  </div>
);

export default Payment;
