import React, { useState, useEffect } from 'react';
import { FaWhatsapp, FaFacebookF, FaTwitter, FaEnvelope } from 'react-icons/fa';

function useLocalUserId() {
  const [id] = useState(() => localStorage.getItem('user_id') || (() => {
    const demoId = 'demo-' + Math.random().toString(36).slice(2, 9);
    localStorage.setItem('user_id', demoId);
    return demoId;
  })());
  return id;
}

function ReferralProgram() {
  const userId = useLocalUserId();
  const [referrals, setReferrals] = useState(() => JSON.parse(localStorage.getItem('referral_history') || '[]'));
  const [copied, setCopied] = useState(false);

  const referralLink = `${window.location.origin}/register?ref=${encodeURIComponent(userId)}`;

  useEffect(() => {
    localStorage.setItem('referral_history', JSON.stringify(referrals));
  }, [referrals]);

  const handleCopy = async () => {
    await navigator.clipboard.writeText(referralLink);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  const addDemoReferral = () => {
    const newRef = {
      id: Math.random().toString(36).slice(2, 9),
      name: 'Friend ' + (referrals.length + 1),
      email: `friend${referrals.length + 1}@example.com`,
      date: new Date().toISOString(),
      rewarded: Math.random() < 0.5,
    };
    setReferrals([newRef, ...referrals]);
  };

  const markRewarded = (id) => {
    setReferrals(referrals.map(r => r.id === id ? { ...r, rewarded: true } : r));
  };

  const totalReferrals = referrals.length;
  const totalRewards = referrals.filter(r => r.rewarded).length;
  const pendingRewards = totalReferrals - totalRewards;

  const qrSvgUrl = `https://chart.googleapis.com/chart?cht=qr&chs=220x220&chl=${encodeURIComponent(referralLink)}&choe=UTF-8`;

  const share = (platform) => {
    const encoded = encodeURIComponent(referralLink);
    let url = '';
    switch (platform) {
      case 'whatsapp':
        url = `https://wa.me/?text=${encoded}`;
        break;
      case 'facebook':
        url = `https://www.facebook.com/sharer/sharer.php?u=${encoded}`;
        break;
      case 'twitter':
        url = `https://twitter.com/intent/tweet?text=Join me! ${encoded}`;
        break;
      case 'email':
        url = `mailto:?subject=Join me!&body=${encoded}`;
        break;
    }
    window.open(url, '_blank', 'noopener,noreferrer,width=600,height=600');
  };

  return (
    <div className="min-h-screen bg-gray-50 p-4 flex justify-center">
      <div className="max-w-4xl w-full bg-white rounded-2xl shadow-lg p-6 space-y-8">

        {/* Stats */}
        <div className="grid grid-cols-1 sm:grid-cols-3 gap-4 text-center">
          <div className="p-4 rounded-xl shadow-sm bg-blue-50 hover:shadow-md transition">
            <div className="text-2xl font-bold text-blue-700">{totalReferrals}</div>
            <div className="text-gray-600 text-sm mt-1">Total Referrals</div>
          </div>
          <div className="p-4 rounded-xl shadow-sm bg-green-50 hover:shadow-md transition">
            <div className="text-2xl font-bold text-green-700">{totalRewards}</div>
            <div className="text-gray-600 text-sm mt-1">Rewards Earned</div>
          </div>
          <div className="p-4 rounded-xl shadow-sm bg-yellow-50 hover:shadow-md transition">
            <div className="text-2xl font-bold text-yellow-700">{pendingRewards}</div>
            <div className="text-gray-600 text-sm mt-1">Pending Rewards</div>
          </div>
        </div>

        {/* Referral link & QR */}
        <div className="flex flex-col md:flex-row items-center gap-4">
          <div className="flex-1 flex gap-2">
            <input
              type="text"
              readOnly
              value={referralLink}
              className="flex-1 p-3 rounded-lg border border-gray-300 text-sm shadow-sm focus:ring-2 focus:ring-indigo-500 focus:outline-none"
            />
            <button onClick={handleCopy} className={`px-4 py-2 rounded-lg bg-indigo-600 text-white shadow hover:bg-indigo-700 transition`}>
              {copied ? 'Copied!' : 'Copy'}
            </button>
          </div>
          <div className="w-40 h-40 bg-white rounded-lg flex items-center justify-center shadow-md">
            <img src={qrSvgUrl} alt="QR Code" className="p-2"/>
          </div>
        </div>

        {/* Social share buttons */}
        <div className="flex flex-wrap gap-3 justify-center md:justify-start">
          <button onClick={() => share('whatsapp')} className="flex items-center gap-2 px-4 py-2 rounded-lg bg-green-500 text-white shadow hover:bg-green-600 transition"><FaWhatsapp /> WhatsApp</button>
          <button onClick={() => share('facebook')} className="flex items-center gap-2 px-4 py-2 rounded-lg bg-blue-700 text-white shadow hover:bg-blue-800 transition"><FaFacebookF /> Facebook</button>
          <button onClick={() => share('twitter')} className="flex items-center gap-2 px-4 py-2 rounded-lg bg-sky-400 text-white shadow hover:bg-sky-500 transition"><FaTwitter /> Twitter</button>
          <button onClick={() => share('email')} className="flex items-center gap-2 px-4 py-2 rounded-lg bg-gray-300 shadow hover:bg-gray-400 transition"><FaEnvelope /> Email</button>
        </div>

        {/* Invite friends button */}
        <div className="text-center">
          <button onClick={addDemoReferral} className="px-6 py-3 bg-indigo-600 text-white rounded-lg shadow hover:bg-indigo-700 transition">
            Invite a Friend
          </button>
        </div>

        {/* Referral history */}
        <div className="space-y-4 max-h-96 overflow-y-auto">
          {referrals.length === 0 && <div className="text-center text-gray-500">No referrals yet</div>}
          {referrals.map(r => (
            <div key={r.id} className="p-4 bg-gray-50 rounded-lg shadow-sm flex flex-col md:flex-row justify-between items-start md:items-center gap-4 hover:shadow-md transition">
              <div>
                <div className="font-semibold">{r.name}</div>
                <div className="text-xs text-gray-500">{r.email}</div>
                <div className="text-xs text-gray-400">{new Date(r.date).toLocaleString()}</div>
              </div>
              <div className="flex items-center gap-2 mt-2 md:mt-0">
                <div className={`px-2 py-1 rounded text-xs font-semibold ${r.rewarded ? 'bg-green-100 text-green-700' : 'bg-yellow-100 text-yellow-700'}`}>
                  {r.rewarded ? 'Rewarded' : 'Pending'}
                </div>
                {!r.rewarded && (
                  <button onClick={() => markRewarded(r.id)} className="px-2 py-1 bg-indigo-600 text-white text-xs rounded hover:bg-indigo-700 transition">Mark Rewarded</button>
                )}
              </div>
            </div>
          ))}
        </div>

      </div>
    </div>
  );
}

export default ReferralProgram;
