import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import API from '../api';
import {Link} from 'react-router-dom';
import './pages.css';

function StudentDashboard() {
  const [searchTerm, setSearchTerm] = useState('');
  const [tutors, setTutors] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    API.get('/tutors')
      .then((res) => {
        setTutors(res.data);
        setLoading(false);
      })
      .catch(() => {
        setTutors([]);
        setLoading(false);
      });
  }, []);

  const filteredTutors = tutors.filter((tutor) =>
    `${tutor.name} ${tutor.subject || ''}`.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <Navbar role="student" />
      <div className="dashboard">
        <header className="dashboard-header">
          Student Dashboard
        </header>

        {/* üîç Search Bar */}
        <div className="mb-6">
          <input
            type="text"
            placeholder="Search tutors by name or subject..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-bar"
          />
          <div style={{marginBottom:20}}>
              <Link to="/TutorSeach" style={{color:'blue'}}>
                Find Tutors
              </Link>
              </div>
        </div>

        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
          {/* Upcoming Sessions */}
          <div className="card">
            <h2>Upcoming Sessions</h2>
            <p>You have 2 sessions scheduled this week.</p>
          </div>

          {/* Available Tutors - with filtered list */}
          <div className="card">
            <h2>Available Tutors</h2>
            {loading ? (
              <p>Loading tutors...</p>
            ) : filteredTutors.length > 0 ? (
              filteredTutors.map((tutor) => (
                <div key={tutor.id}>
                  <p>{tutor.name}</p>
                  <p>{tutor.subject || 'No subject listed'}</p>
                </div>
              ))
            ) : (
              <p>No tutors match your search.</p>
            )}
          </div>

          {/* My Progress */}
          <div className="card">
            <h2>My Progress</h2>
            <p>Track your learning and goals.</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default StudentDashboard;
